INSERT INTO restaurants (id, name, address, phone, description, avatar, user_id, created_at, updated_at) VALUES
(1, 'Bò Tơ Quán Mộc', '1A Tăng Bạt Hổ, Phạm Đình Hổ, Hai Bà Trưng, Hà Nội', '094 581 3355', 'Bạn đang tìm kiếm một quán ăn chuyên phục các món về bò, với không gian đậm chất Hà Nội xưa hãy đến ngay Bò Tơ Quán Mộc. Đây sẽ là lựa chọn hoàn hảo dành cho bạn. Bò Tơ Quán Mộc được biết đến với thực đơn đa dạn,g từ chất lượng đến hương vị các món ăn đều được khách hàng đánh giá rất cao. Nhờ bàn tay khéo léo và tài tình của đầu bếp đã biến những món ăn quen thuộc trở nên hấp dẫn hơn bao giờ hết.nThực đơn Bò Tơ Quán Mộc cực kỳ đặc sắc và hấp dẫn, với hơn 40 món ẩm thực độc đáo được làm từ thịt bò tơ tươi, non, ngon, sạch. Trong đó bạn nhất định không thể bỏ qua các món ăn như: Dẻ sườn nướng, Lẩu bò tươi xanh, Bò tơ tái chanh, Bò tơ hấp cuốn bánh trang, Bò thần công…… Đi theo nhóm đông người sẽ giúp bạn thưởng thức được nhiều món ngon hơn đó nha!nKhông gian quán khá rộng rãi với diện tích hơn 1000m2. Được trang trí theo phong cách những căn nhà cổ Hà Nội. Với đèn vàng lung linh, gạch hoa quen thuộc, tổng thể không gian mang đến cho khách hàng cảm giác gần gũi, ấm cúng, gắn liền với những hình ảnh Hà Nội xưa. Sức chứa của quán khá lớn, lên đến 400 khách hàng. Đội ngũ nhân viên nhiệt tình, chu đáo. Điểm 10 cho phục vụ, điểm 10 cho chất lượng.', 'restaurant165312131666.jpg', 1, '2022-05-21 01:23:53', '2022-05-21 01:23:53'),
(2, 'Rice Bistro', '32 Hàng Mành, Hoàn Kiếm, Hà Nội.', '0243 928 8912', 'Bạn là người yêu ẩm thực truyền thống của người Việt? Và muốn tìm một không gian sang trọng để thưởng thức những món ăn chuẩn Việt cùng bạn bè trong một bữa tối đáng nhớ? Mở đầu danh sách, Diachiamthuc.vn xin gửi tới các bạn Rice Bistro. Rice Bistro chính là điểm đến lý tưởng cho bạn đấy. Với đội ngũ đầu bếp tài ba giàu kinh nghiệm, nhà hàng Rice Bistro đã tạo nên những món ăn đậm vị Việt nhưng lại được bài trí khéo léo, tinh tế theo kiểu phương Tây trang nhã.Đó thực sự là những món ăn vừa ngon mắt, lại ngon miệng. Tất cả khiến bạn mê mẩn ngay từ lần đầu. Đến với nhà hàng bạn sẽ cảm nhận được những món ăn mang sự tinh tế với hương vị thanh đạm của ẩm thực miền Bắc. Bạn cũng sẽ say mê hương vị đậm đà, cay nồng của ẩm thực miền Trung. Và không thể thiếu những món ăn miền Nam dân dã nhưng cực kì ngon.Nhìn vào menu, bạn thoải mái gọi cho mình phở bò, phở gà, bún thang, gỏi cuốn, các món nộm… và vô số món ăn chuẩn Việt khác. Không gian của Rice Bistro được thiết kế rất hiện đại, ấm áp, chiều lòng tất cả mọi người. Một bữa tối tại Rice Bistro sẽ giúp bạn tận hưởng cuộc sống trọn vẹn hơn.', 'restaurant165312183061.jpg', 1, '2022-05-21 01:33:02', '2022-05-21 01:33:02'),
(3, 'Nhà hàng Xanh Garden', 'Thôn Trường An, Xã An Khánh, huyện Hoài Đức, Hà Nội.', '093 250 6569', 'Nếu bạn muốn thiết kế một bữa ăn tối cùng những người thân yêu ở một nơi xa trung tâm thành phố một chút để có không gian yên tĩnh. Vậy mời bạn đến với nhà hàng Xanh Garden. Nhà hàng Hà Nội này nằm ở Hoài Đức và cũng là một trong những nhà hàng ngon nhưng giá cả vô cùng hợp lý mà bạn nên thử. Những buổi tối sau giờ tan sở, cùng những người thân yêu ngồi thưởng thức bữa tối nhàn hạ trong không gian rộng lớn với lối kiến trúc cực kì đẹp của Xanh thì còn gì bằng.Đây còn là nơi thích hợp để bạn chọn tổ chức bữa tiệc cưới hỏi, sinh nhật, tiệc gia đình, liên hoan hay các bữa buffet ngoài trời. Đến với xanh, bạn sẽ được thoả mãn mọi thị hiếu ẩm thực. Menu gồm những món hải sản tươi sống, ngon đến miếng cuối cùng. Các bạn cũng có thể thưởng thức các món độc lạ trên rừng dưới biển. Chưa hết, nếu là tín đồ món lẩu bạn cũng tha hồ thưởng thức lẩu hơi, lẩu nướng.Tất cả được bài trí đẹp mắt trong một không gian cực kỳ đẳng cấp. Bạn chắc chắn sẽ hài lòng về trình độ nấu ăn, nên nếm và bày biện của đội ngũ đầu bếp giàu kinh nghiệm và tận tâm nơi đây. Ngoài việc được thưởng thức những món ngon khó cưỡng, đến với Xanh bạn còn được trải nghiệm thêm nhiều tiện ích tuyệt vời khác. Đó là tổ hợp Gym, yoga và khu vui chơi của trẻ em.Nếu yêu âm nhạc, bạn và người thân còn có thể hát Karaoke ngay tại những căn phòng xịn xò đã được chuẩn bị sẵn tại đây. Bởi tích hợp rất nhiều tiện ích như vậy nên Xanh Garden đang là điểm đến hấp dẫn cho những thực khách muốn có một buổi tối thư giãn trọn vẹn. Đặc biệt nhất là một suất ăn ở đây chỉ có giá từ 149k thôi nhé.', 'restaurant165312241137.jpg', 1, '2022-05-21 01:40:21', '2022-05-21 01:40:21'),
(4, 'Nhà hàng Chen', 'số 17 Thái Phiên, Hà Nội', '0917 146 956', 'Đây là nhà hàng đã có lịch sử hơn một thập kỷ trên đất thủ đô. Cùng với thời gian Chen đã khẳng định được vị trí vững chắc của mình trong lòng những người yêu ẩm thực ở Hà Nội. Đến nay nhà hàng Chen đã trở thành một trong những nhà hàng 5 sao bậc nhất Hà Thành. Bạn biết không? Đây chính là nhà hàng đầu tiên mang phong cách nướng không khói của Nhật đến với Hà Nội đấy.Phong cách này ngay lập tức đã được đông đảo bạn trẻ và thực khách yêu thích. Với bàn nướng Teppaniyaki, đội ngũ đầu bếp của Chen đã chế biến ra những món nướng tuyệt hảo bằng kỹ xảo nấu ăn đỉnh cao. Các bạn cũng có thể thoả mãn đam mê về các món Nhật như Sushi hay Sashimi tại đây. Không gian của Chen thì miễn bàn về độ sang trọng. Nhà hàng gồm 4 tầng mặt kính, đem lại view xịn xò cho bữa ăn tối của bạn.Đến nhà hàng, bạn sẽ thấy ngay khẩu hiệu nổi tiếng “Hãy nấu như nấu cho người bạn yêu thương nhất”. Điều đó lí giải vì sao các món ăn ở đây đều được nấu bằng cả tâm huyết của đội ngũ đầu bếp chuyên nghiệp. Bạn sẽ không bao giờ quên được hương vị của các món ăn nơi đây. Đừng bỏ lỡ bữa tối tại không gian sang trọng này trong những dịp đáng nhớ nhé bạn.', 'restaurant165312267068.jpg', 1, '2022-05-21 01:45:40', '2022-05-21 01:45:40'),
(5, 'nhà hàng Hải Cảng', 'Tầng 12, Tòa nhà Sun City, số 13 Hai Bà Trưng, Hoàn Kiếm, Hà Nộ', '02438249988', 'Bạn là tín đồ của ẩm thực Hồng Kông? Vậy thì bữa tối tại chuỗi nhà hàng Hải Cảng chắc chắn phù hợp với bạn. Đây là chuỗi nhà hàng năm sao nổi tiếng tại Hà Nội. Không gian ở đây cực kỳ sang trọng, được thiết kế theo kiến trúc cung đình. Nhà hàng có 12 phòng Vip đạt chuẩn đẳng cấp 5 sao và không gian rộng rãi với sức chứa lên tới 500 thực khách cùng lúc. Bởi vậy, nếu bạn đang chuẩn bị cho một buổi tối hẹn hò, hội nghị, liên hoan hay gặp đối tác quan trọng thì hãy đến với Hải Cảng. View của nhà hàng cực kì đẹp, bàn ghế và đồ đạc bên trong bày biện đúng kiểu vương giả sang trọng. Nhà hàng này cũng đã có bề dày lịch sử gần 2 thập kỷ, trở thành điểm đến lý tưởng của thực khách yêu ẩm thực Hồng Kông nói riêng, ẩm thực châu Á nói riêng. Nguyên liệu của các món ăn được chọn lựa kỹ càng từ trại Hải Cảng Đồng Mô. Đến đây, bạn sẽ bắt gặp những nguyên liệu hết sức cao cấp như hải sâm, bào ngư, tổ yến…Từ những nguyên liệu tươi ngon ấy, đội ngũ đầu bếp tài hoa sẽ tạo ra những món ăn đảm bảo các tiêu chí: tinh, chuẩn và tốt cho sức khỏe. Bạn còn ngần ngại gì không ghé qua một trong những địa điểm sang trọng của Hải Cảng để thưởng thức một bữa tối hoàn hảo?', 'restaurant165312301997.jpg', 1, '2022-05-21 01:51:38', '2022-05-21 01:51:38'),
(6, 'Nhà hàng Long Vĩ', '3A Đào Duy Anh, Đống Đa, Hà Nội', '0947884646', 'Thêm một nhà hàng nữa cho bạn chọn chính là Long Vĩ. Nhà hàng được xây dựng trong một khuôn viên rộng rãi hơn 1.600m2. Nhà hàng gồm hai tầng được thiết kế theo kiến trúc hiện đại. Xung quanh bao phủ bởi những hàng cây xanh thân thiện với thiên nhiên. Phong cách bài trí của nhà hàng rất tươi sáng, trang nhã nhưng cũng cực kỳ lộng lẫy, ấn tượng đến từ bàn ghế và đèn chùm trang trí. Ngoài không gian sảnh tiệc chung và không gian phòng Vip cho những bữa ăn riêng tư, nhà hàng còn có quầy bar rộng rãi để thực khách thưởng thức các loại đồ uống ngon. Món tủ ở đây là hải sản. Được chế biến bằng hương vị của cả ba miền Bắc Trung Nam. Nguyên liệu được lựa chọn kỹ càng, cách chế biến cẩn thận, nêm nếm chuẩn vị tạo ra những bữa ăn ngon làm hài lòng mọi thực khách. Đầu bếp của nhà hàng đã từng tu nghiệp tại nước ngoài, bởi vậy bàn tay vàng của họ tạo ra những món ăn cực kì hấp dẫn.Mỗi món ăn đều là sự kết hợp giữa vị ngọt của nguyên liệu, đạm đà của gai vị và khéo léo của trình bày. Đến với nhà hàng bạn sẽ bắt gặp những nguyên liệu hải sản cực xịn như: Tôm Mũ ni Đỏ, Xanh, Alaska, cá lưỡi trâu, cá thờn bơn Nhật, Tu Hài Canada, cua Huỳnh Đế, ốc móng tay… Hàng trăm món hải sản ngon miệng trong một không gian sang trọng đang chờ bạn đến thưởng thức bữa tối tại đây đấy!', 'restaurant165312337142.jpg', 1, '2022-05-21 01:57:21', '2022-05-21 01:57:21'),
(7, 'Nét Huế', '198 Hàng Bông, Q. Hoàn Kiếm, Hà Nội', '02439381795', 'Đây là chuỗi nhà hàng chuyên món ăn Huế với 9 cửa hàng tại Hà Nội. Không gian của quán hết sức rộng rãi, được thiết kế và bài trí đẹp mắt. Nơi đây hội tụ đầy đủ tinh hoa ẩm thực Huế từ dân dã đến cung đình. Món Huế xưa nay có sức gợi thương gợi nhớ bởi hương vị thuần khiết. Ẩm thực Huế được biết đến với đủ vị chua, cay, mặn, ngọt, đắng, thơm… Mỗi món ăn Huế hội tụ sự cầu kỳ, thanh khiết và đẹp mắt, tinh tế như chính tâm hồn người Huế vậy.Nhà hàng Nét Huế đã mang trọn cái phong vị tinh tế trong ẩm thực Huế ấy về giữa Hà Nội phồn hoa. Những món ăn của nhà hàng Nét Huế nhanh chóng được lòng những thực khách Hà Thành vốn tao nhã và cũng không kém phần tinh tế. Đến đây, bạn nhớ đừng bỏ qua chén cơm hến, tô bún bò Huế đậm vị, hay món bánh khoái, bánh nậm, bánh bèo… Tất cả sẽ quyến rũ bạn không ngừng, níu chân bạn trở lại nhiều lần sau đó.', 'restaurant165312360981.jpg', 1, '2022-05-21 02:00:53', '2022-05-21 02:00:53'),
(8, 'Nhà hàng L’Annam Buffet', '117 Bùi Thị Xuân, Quận Hai Bà Trưng, Hà Nội', '0984 901 157', 'Đây vốn là nhà hàng Sen Hà Thành quen thuộc. Nhà hàng là địa chỉ thân quen và được yêu mến của thực khách Hà Nội bởi nét độc đáo trong món ăn và cách chế biến. Được thiết kế theo phong cách kiến trúc châu Âu cổ xưa, nhà hàng có sức chứa khoảng 250 khách. Đặc biệt nhất ở đây chính là buffet hải sản mang phong cách châu Á. Bạn sẽ bắt gặp một quần Sushi, Sashimi và gỏi cá hồi. Thêm một quầy cá trích tươi sống và một quầy nướng hải sản.Menu của nhà hàng cực kì phong phú với hơn 100 món. Nguyên liệu của nhà hàng được nhập hàng ngày, luôn đảm bảo độ tươi sống. Đến đây bạn sẽ được thưởng thức mọi loại hải sản từ ốc vòi voi, baba, tôm hùm, cá trình đến sò, ngao, các loại cá. Nhà hàng chế biến đủ món ăn theo kiểu Á Âu tuỳ khẩu vị của bạn. Một suất ăn ở đây giáo động từ 265k đến 350k. Đây là mức giá hợp lý cho một nhà hàng sang trọng và thực đơn hải sản tươi ngon.', 'restaurant165312387966.jpg', 1, '2022-05-21 02:05:19', '2022-05-21 02:05:19'),
(9, 'Thế giới hải sản', '75A Trần Hưng Đạo, Hoàn Kiếm, HN', '090 604 26 26', 'Đây là nhà hàng thuộc công ty cổ phần xuất nhập khẩu Thế Giới Hải Sản. Thế giới hải sản là địa chỉ ẩm thực nổi tiếng của dân sành ẩm thực Hà Thành. Hải sản ở đây được chế biến từ nguyên liệu cao cấp và hoàn toàn tươi sống. Các bạn có thể bắt gặp hơn 100 loại hải sản tươi sống chất lượng loại 1. Tất cả được thả sống trong các bể kính và chăm sóc bởi đội ngũ kỹ sư thuỷ sản. Menu của nhà hàng cực kì phong phú. Các món ăn được chế biến từ nguyên liệu cao cấp, tỉ mỉ, bài trí đẹp mắt.Nhân viên của nhà hàng rất trẻ trung, năng động và nhiệt tình. Thế giới hải sản tự hào là nhà hàng 5 chữ Nhất: đa dạng nhất, tươi ngon nhất, chất lượng nhất, giá cả hợp lý nhất và đông khách nhất. Bạn hãy cùng bạn bè và người thân đến thưởng thức bữa tối tại đây để cảm nhận những loại hải sản cực ngon dưới bàn tay khéo léo của đội ngũ đầu bếp nhiều kinh nghiệm.', 'restaurant165312473629.png', 1, '2022-05-21 02:09:41', '2022-05-21 02:19:06'),
(10, 'Nhà hàng Sơn Thiên', '37 Trần Kim Xuyến, Phường Yên Hòa, Quận Cầu Giấy, Hà Nội', '096 142 91 58', 'Nằm ngay trung tâm quận Cầu Giấy, các bạn đừng bỏ qua nhà hàng ẩm thực Sơn Thiên Trần Kim Xuyến. Nhà hàng tọa lạc trên một diện tích rộng trên 2000m2, mang phong cách vừa cổ điển vừa hiện đại. Nhà hàng hội tụ nhiều món ngon truyền thống, tinh hoa đặc sắc của cả 3 miền. Đến đây, bạn sẽ cảm thấy bình yên khi bắt gặp những bức tường gạch, những chiếc nón lá, những cây tre, cây trúc mang âm hưởng kiến trúc dân gian đương đại. Tiêu chí phục vụ ẩm thực của quán là: “Ngon lành, bổ dưỡng, an toàn vệ sinh”. Tất cả các món ăn ở đây đều được chế biến từ nguồn nguyên liệu tươi mới, an toàn. Các bạn có thể thưởng thức những món hải sản tươi sống, quý hiếm hay ăn các đặc sản núi rừng dưới bàn tay chế biến tài hoa của người đầu bếp. Món ăn không chỉ ngon, trang trí đẹp mắt mà còn cực kì an toàn cho sức khoẻ của thực khách. Thưởng thức bữa tối với những sản vật trên rừng dưới biển trọng một không gian thoáng đãng của Sơn Thiên không phải là gợi ý khiến bạn thất vọng đâu.', 'restaurant165312463139.png', 1, '2022-05-21 02:17:43', '2022-05-21 02:17:43'),
(11, 'Sen Hồ Tây', '614 Lạc Long Quân, Quận Tây Hồ, Hà Nội', '024 3719 9242', 'Nếu muốn tìm một nhà hàng hải sản ngon nổi tiếng Hà Nội thì bạn chắc chắn đừng bỏ qua nhà hàng sen Tây Hồ. Đây cũng là nhà hàng buffet rộng, được yêu thích và đông khách nhất Hà Nội. Nhà hàng nằm ngay cạnh Hồ Tây với view cực kì đẹp và thoáng. Khuôn viên của nhà hàng chia thành nhiều khu với thiết kế trang trọng mang phong cách Châu Âu. Với giới trẻ và những người thích sống ảo thì đến đây bạn còn có thêm hàng trăm tấm ảnh sống ảo cực sang xịn nữa đấy. Nhà hàng có ba khu vực chính: Khu Hà nội, khu Quốc tế và khu đại tiệc sao. Menu của nhà hàng có hàng trăm món, hội tụ tinh hoa ẩm thực từ Bắc vào Nam. Buffet của nhà hàng rất đa dạng trong món ăn. Đội ngũ đầu bếp của nhà hàng chế biến món ăn theo khẩu hiệu: “Vị ngọt trong khẩu vị, vị ngọt trong tinh thần.Bởi vậy các món ăn đều được chế biến tỉ mỉ, khéo léo, hội tụ đầy đủ tinh túy của món ăn. Không gian của nhà hàng hội tụ sự kết hợp của lụa, hoa, nến và âm nhạc du dương cổ điển đầy lãng mạn. Đội ngũ nhân viên của nhà hàng cũng rất chuyên nghiệp, nhiệt tình. Tất cả mang đến cho bạn một bữa tối như ý.', 'restaurant165312502311.jpg', 1, '2022-05-21 02:24:31', '2022-05-21 02:24:31'),
(12, 'Nhà hàng Phương Nguyên', '51 – 53 Tô Ngọc Vân, Quận Tây Hồ, Hà Nội', '098 365 00 94', 'Đây là địa chỉ ẩm thực đã có mặt hơn 25 năm giữa lòng Hà Nội. Xuất phát điểm chỉ là một quán ăn nhỏ nằm giao giữa Hồ Tây và Hồ Quảng Bá. Ngày nay nhà hàng Phương Nguyên đã có chỗ đứng vững chắc trong lòng thực khách Hà thành. Không gian của nhà hàng hướng ra Hồ Tây, view cực đẹp. Phong cách của đội ngũ nhân viên lại cực kỳ chuyên nghiệp. Menu của nhà hàng ưu tiên những món Việt đặc sắc khiến bạn hài lòng ngay. Các bạn có thể chọn từ những món khai vị như súp, salad, rau củ đến những món chính như hải sản, thịt gà, bò, dê hay cơm, mì, cháo, lẩu… Tất cả đều được chế biến kxi càng, hương vị đậm đà, trang trí bắt mắt. Nhưng đến với Phương Nguyên bạn đừng quên gọi những món tủ ở đây như Tôm chiên trứng muối, gà nướng mật ong, bò lúc lắc, cá chép chiên Hồ Tây, Cá tầm rang muối, cua bấy chiên bơ, sò huyết nướng, nhím hấp… Dù là món ăn dân dã hay hiện đại thì cũng đều được các đầu bếp đầu tư hết tâm huyết. Không chỉ vậy, nơi đây còn mang lại cho bạn các món ăn với hương vị quốc tế độc đáo như sú chiên kiểu Nhật, dê khâu nhục Trung hoa, bò chiên kiểu Thái, baba tần thuốc Bắc, tần nấm Tứ Xuyên, cơm chiên Dương Châu. Và cũng đừng quên khám phá hầm rượu cưới hàng trăm loại rượu vang của Phương Nguyên nhé. Đồ ăn ngon, rượu vang chuẩn vị, không gian đẹp, tiếc gì bạn không chọn Phương Nguyên phải không nào?', 'restaurant165312529283.jpg', 1, '2022-05-21 02:29:04', '2022-05-21 02:29:04');